import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Adjust the URL as needed

export const registerUser = async (userData) => {
    try {
        const response = await axios.post(`${API_URL}/register`, userData);
        return response.data;
    } catch (error) {
        throw error.response?.data || error;
    }
};

export const makeDonation = async (donationData) => {
    try {
        const response = await axios.post(`${API_URL}/donate`, donationData);
        return response.data;
    } catch (error) {
        throw error.response?.data || error;
    }
};

// Add this function for your Donation component
export const donate = async (amount) => {
    try {
        // You can add more fields if needed
        const response = await makeDonation({ amount });
        return response;
    } catch (error) {
        return { success: false, error };
    }
};

export const fetchGalleryItems = async () => {
    try {
        const response = await axios.get(`${API_URL}/gallery`);
        return response.data;
    } catch (error) {
        throw error.response?.data || error;
    }
};

export const uploadGalleryItem = async (formData) => {
    try {
        const response = await axios.post(`${API_URL}/gallery/upload`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        });
        return response.data;
    } catch (error) {
        throw error.response?.data || error;
    }
};
