import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Adjust the URL as needed

// Optionally, define types for better TypeScript support
export interface Member {
    id: string;
    name: string;
    // Add other fields as needed
}

export interface GalleryItem {
    id: string;
    title: string;
    // Add other fields as needed
}

export const registerUser = async (userData: any) => {
    try {
        const response = await axios.post(`${API_URL}/register`, userData);
        return response.data;
    } catch (error: any) {
        throw error.response?.data || error;
    }
};

export const makeDonation = async (donationData: any) => {
    try {
        const response = await axios.post(`${API_URL}/donate`, donationData);
        return response.data;
    } catch (error: any) {
        throw error.response?.data || error;
    }
};

// For your Donation component
export const donate = async (amount: string) => {
    try {
        const response = await makeDonation({ amount });
        return response;
    } catch (error: any) {
        return { success: false, error };
    }
};

export const fetchGalleryItems = async (): Promise<GalleryItem[]> => {
    try {
        const response = await axios.get(`${API_URL}/gallery`);
        return response.data;
    } catch (error: any) {
        throw error.response?.data || error;
    }
};

export const uploadGalleryItem = async (formData: any) => {
    try {
        const response = await axios.post(`${API_URL}/gallery/upload`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        });
        return response.data;
    } catch (error: any) {
        throw error.response?.data || error;
    }
};

export const getMembers = async (): Promise<Member[]> => {
    const response = await axios.get(`${API_URL}/members`);
    return response.data;
};

export { fetchGalleryItems as getGalleryItems };
