// src/utils/api.ts
import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api'; // change to your backend URL

// Send donation data to backend
export const donate = async (data: any) => {
  try {
    const res = await axios.post(`${API_BASE_URL}/donate`, data);
    return res.data;
  } catch (err) {
    console.error('Donation API Error:', err);
    throw err;
  }
};

// Get all members (for AdminDashboard)
export const getMembers = async () => {
  try {
    const res = await axios.get(`${API_BASE_URL}/members`);
    return res.data;
  } catch (err) {
    console.error('Get Members Error:', err);
    throw err;
  }
};

// Add more APIs as needed...
