import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Adjust if needed

export interface Member {
    id: string;
    name: string;
    // Add other fields as needed
}

export interface GalleryItem {
    id: string;
    title: string;
    // Add other fields as needed
}

// Safe error extractor
function extractErrorData(error: unknown): any {
    const err = <any>error;
    return err?.response?.data || err;
}

export async function registerUser(userData: any) {
    try {
        const response = await axios.post(`${API_URL}/register`, userData);
        return response.data;
    } catch (error) {
        console.error("Error registering user:", error);
        throw error;
    }
}



export async function makeDonation(donationData: any) {
    try {
        const response = await axios.post(`${API_URL}/donate`, donationData);
        return response.data;
    } catch (error) {
        throw extractErrorData(error);
    }
}

export async function donate(amount: string) {
    try {
        const response = await makeDonation({ amount });
        return response;
    } catch (error) {
        return { success: false, error: extractErrorData(error) };
    }
}

export async function fetchGalleryItems(): Promise<GalleryItem[]> {
    try {
        const response = await axios.get(`${API_URL}/gallery`);
        return response.data;
    } catch (error) {
        throw extractErrorData(error);
    }
}

export async function uploadGalleryItem(formData: any) {
    try {
        const response = await axios.post(`${API_URL}/gallery/upload`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        });
        return response.data;
    } catch (error) {
        throw extractErrorData(error);
    }
}

export async function getMembers(): Promise<Member[]> {
    try {
        const response = await axios.get(`${API_URL}/members`);
        return response.data;
    } catch (error) {
        throw extractErrorData(error);
    }
}

export { fetchGalleryItems as getGalleryItems };
