import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Change this if backend is deployed

// ----------- Members -----------
export const getMembers = async () => {
  try {
    const response = await axios.get(`${API_URL}/members`);
    return response.data;
  } catch (error: any) {
    console.error("Failed to fetch members:", error.response?.data || error.message);
    throw error;
  }
};

export const createMember = async (data: any) => {
  try {
    const response = await axios.post(`${API_URL}/members`, data);
    return response.data;
  } catch (error: any) {
    console.error("Failed to create member:", error.response?.data || error.message);
    throw error;
  }
};

// ----------- Gallery Items -----------
export const getGalleryItems = async () => {
  try {
    const response = await axios.get(`${API_URL}/gallery`);
    return response.data;
  } catch (error: any) {
    console.error("Failed to fetch gallery items:", error.response?.data || error.message);
    throw error;
  }
};

export const uploadGalleryItem = async (data: FormData) => {
  try {
    const response = await axios.post(`${API_URL}/gallery`, data, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    return response.data;
  } catch (error: any) {
    console.error("Failed to upload gallery item:", error.response?.data || error.message);
    throw error;
  }
};

// ----------- Events -----------
export const getEvents = async () => {
  try {
    const response = await axios.get(`${API_URL}/events`);
    return response.data;
  } catch (error: any) {
    console.error("Failed to fetch events:", error.response?.data || error.message);
    throw error;
  }
};

export const createEvent = async (data: any) => {
  try {
    const response = await axios.post(`${API_URL}/events`, data);
    return response.data;
  } catch (error: any) {
    console.error("Failed to create event:", error.response?.data || error.message);
    throw error;
  }
};
